mapper.insert:
INSERT INTO t_board
    (bno, bcd, uno, btitle, bctnt, bidx, bord, bdept)
VALUES
((SELECT NEXTVAL(iboard_seq))
,#{bcd}
,#{uno}
,CONCAT('└ Re : ',#{btitle})
,#{bctnt}
,#{bidx}
,#{bord}+1
,#{bdept}+1
)

mapper.update:
UPDATE t_board
SET bord = bord+1
WHERE bidx = #{bidx}
AND bord > #{bord}

UPDATE t_board
SET bdel = 1
WHERE bno = #{bno} AND (uno = #{uno}
    OR bpw = #{bpw}
    OR (select authno from t_user where uno = #{uno}) = 1
    )

UPDATE t_board
SET btitle = #{btitle}, bctnt = #{bctnt}
WHERE bno = #{bno} AND (uno = #{uno}
    OR bpw = #{bpw}
    OR (select authno from t_user where uno = #{uno}) = 1
    )
