mapper.update:
-- 수정
   UPDATE t_board_category
   SET bnm = #{bnm}
   WHERE bcd = #{bcd}


UPDATE t_board_category
SET cord = cord + 1
WHERE cord > #{cord}


UPDATE t_board_category
SET cord = cord - 1
WHERE cord < #{cord}

mapper.select:
SELECT A.bno, A.uno, A.bcd, A.btitle, A.bctnt, A.bidx, A.bord , A.bdept, A.brdt, A.bhit, A.bdel , B.unm as writer FROM t_board as A LEFT JOIN t_user as B ON A.uno = B.uno where A.bcd = #{bcd} AND A.bdel = 0 AND A.bcd = #{bcd}
-- TODO:검색식 추가
ORDER BY A.bidx desc, A.bord asc LIMIT #{startIdx}, #{cntPerPage}
