mapper.insert:
INSERT INTO t_board
    (bno, bcd, uno, btitle, bctnt, bidx, bpw)
VALUES ( (SELECT NEXTVAL(iboard_seq))
       , #{bcd}
       , #{uno}
       , #{btitle}
       , #{bctnt}
       , (SELECT LASTVAL(iboard_seq))
       , #{bpw})

INSERT INTO t_board
    (bno, bcd, uno, btitle, bctnt, bidx, bord, bdept, bpw)
VALUES ( (SELECT NEXTVAL(iboard_seq))
       , #{bcd}
       , #{uno}
       , #{btitle}
       , #{bctnt}
       , #{bidx}
       , #{bord} + 1
       , #{bdept} + 1
     , #{bpw} )

mapper.select:
SELECT A.bno, A.uno, A.bcd, A.btitle, A.bctnt, A.bidx, A.bord , A.bdept, A.brdt, A.bhit , B.unm as writer , (SELECT COUNT(C.bno) FROM t_board_recommendation AS C WHERE C.bno = A.bno AND rec = 1) AS rec FROM t_board as A LEFT JOIN t_user as B ON A.uno = B.uno where A.bdel = 0 AND A.bcd = #{bcd}
AND (A.btitle like CONCAT('%',#{searchText},'%') OR A.bctnt like CONCAT('%',#{searchText},'%'))
AND A.btitle like CONCAT('%',#{searchText},'%')
AND A.bctnt like CONCAT('%',#{searchText},'%')
AND B.unm like CONCAT('%',#{searchText},'%')
ORDER BY A.bidx desc, A.bord asc LIMIT #{startIdx}, #{cntPerPage}

mapper.update:
UPDATE t_board
SET bord = bord + 1
WHERE bidx = #{bidx}
  AND bord > #{bord}

UPDATE t_board
SET bhit = bhit + 1
where bno = #{bno}
