mapper.select:
select uid
from t_user
where uemail = #{uemail}
  and uid = #{uid}

select uid
from t_googleuser
where uid = #{uid}

select *
from t_user
where uemail = #{uemail}
  and authkey = #{authkey};

mapper.update:
update t_user
set authno = 3
where authkey = #{authkey}
  and uemail = #{uemail}

update t_user
set upw = #{upw}
where uemail = #{uemail} or uid=#{uid}

update t_user
set profileImg=#{profileImg}
where authno = #{authno}

mapper.select:
select profileImg
from t_user
where authno = #{authno}
